@bot.command(help="Morse Decode")
async def md(ctx,*,sub):
	sub2,dm="",""
	for i in sub:
		if i==".":
			i="‚Ä¢"
		if i=="_":
			i="-"
		sub2="".join([sub2,i])
	wl=sub2.split("\n")
	for word in wl:
		cl=word.split()
		for c in cl:
			for x,y in morse.items():
				if y==c:
					dm+=x
		dm+="\n"
	embed=discord.Embed(title="Decoded Message",description=dm,color=discord.Color.blue())
	await ctx.send(embed=embed)
